+}
+
+#PanelUI-button #whats-new-menu-button:not([hidden="true"]) {
+  margin-inline-end: 3px !important;
+}
+
+/* back button adjustment */
+#nav-bar #back-button .toolbarbutton-icon {
+  border: 0 !important;
+}
+
