+    ) !important;
+  border-radius: 6px !important;
+  outline: 1px solid rgba(124, 163, 206, 0.7) !important;
+  transition: background-color 0.3s ease-in, border-color 0.2s ease-in,
+    box-shadow 0.2s ease-in !important;
+}
+
+#nav-bar
+  toolbarbutton:not(#PlacesChevron)[type="menu"][open]:not([disabled]):not(.bookmark-item):not([container="true"])
+  > .toolbarbutton-icon,
+#nav-bar
