+.tabbrowser-tab:focus:not([aria-activedescendant]) .tab-content {
+  box-shadow: 0px 0px 25px 3px rgba(0, 0, 0, 0.1),
+    0px 0px 15px 0px hsla(0, 0%, 0%, 0.35) !important;
+}

